lines = s.split("\n")
M = len(lines)
N = len(lines[0])

import numpy as np

l = []
arr = np.zeros((M,N))
for i in range(M):
    for j in range(N):
        if lines[i][j] == "#":
            arr[i][j] = 1
            l.append((i,j))

ver = np.sum(arr, axis=1)
hoz = np.sum(arr, axis=0)

ans = 0
for index,(i,j) in enumerate(l):
    for index2, (ii,jj) in enumerate(l):
        # print(i,ii,j,jj)
        hdis = abs(ii-i)
        for iii in range(min(i,ii),max(i,ii)):
            if ver[iii] == 0:
                hdis += 1 * 999999
                # print(iii)
            # hdis += 1
        vdis = abs(jj-j)
        for jjj in range(min(j,jj),max(j,jj)):
            if hoz[jjj] == 0:
                vdis += 1 * 999999
            # vdis += 1
        old_ans = ans
        # print(vdis, hdis)
        # print(ver, hoz)
        ans += vdis + hdis
